const chatId = $input.first().json.message.chat.id;
const text = $input.first().json.message.text;
const sessionKey = `session_${chatId}`;

// In production, retrieve session from Redis/database
// For now, check if message looks like an answer
const session = {
  chatId,
  course: 'variables',
  currentQuestion: 'What is the difference between let and const?',
  retentionHistory: []
};

if (!session.course) {
  return {
    chatId,
    message: "‚ùå No active course. Use /learn [course] to start.",
    parseMode: 'Markdown',
    skipTutoring: true
  };
}

return {
  chatId,
  course: session.course,
  question: session.currentQuestion,
  answer: text,
  session
};

const chatId = $input.first().json.chatId;
const tutoringResponse = $input.last().json;

const retention = tutoringResponse.retention || 0;
const feedback = tutoringResponse.feedback || 'Good effort!';
const lessonKind = tutoringResponse.lesson_kind || 'review';

const retentionPercent = Math.round(retention * 100);
const emoji = retentionPercent > 75 ? 'üü¢' : retentionPercent >= 50 ? 'üü†' : 'üî¥';

let message = `${emoji} *Retention: ${retentionPercent}%*\n\n`;
message += `üìù ${feedback}\n\n`;

// Add lesson content
if (tutoringResponse.lesson_content) {
  const lessons = Array.isArray(tutoringResponse.lesson_content) 
    ? tutoringResponse.lesson_content 
    : [tutoringResponse.lesson_content];

  lessons.forEach(lesson => {
    if (lesson.title && lesson.content) {
      message += `\n*${lesson.title}*\n${lesson.content}\n`;
    }
  });
}

// Add next question
if (tutoringResponse.questions && tutoringResponse.questions.length > 0) {
  const nextQ = tutoringResponse.questions[0];
  message += `\n\n‚ùì *Next Question:*\n${nextQ.question}`;
}

return {
  chatId,
  message,
  parseMode: 'Markdown'
};
