// Get evaluation data from previous node
const evaluationResponse = $json.response;

// Parse evaluation JSON
let evaluationData = {};
try {
  // First try to parse as direct JSON
  evaluationData = JSON.parse(evaluationResponse);
} catch (e) {
  try {
    // Fallback: try to extract from markdown code blocks
    const jsonMatch = evaluationResponse.match(/```json\s*([\s\S]*?)\s*```/);
    if (jsonMatch) {
      evaluationData = JSON.parse(jsonMatch[1]);
    }
  } catch (e2) {
    console.log('Error parsing evaluation:', e2);
    evaluationData = { feedback: 'Parse error', retention: 0 };
  }
}

// Store evaluation data for later use
return {
  json: {
    ...evaluationData,
    originalResponse: evaluationResponse
  }
};

